<script>
  import util from '@xianyu/assets/util'
  var navigator = weex.requireModule('navigator')
  export default {
    props: ['head', 'hasMore', 'goods'],
    data () {
      return {
        position_img_base64: 'data:image/png;base64,'
      }
    },
    methods: {
      jumpWeb (_url) {
        if (!_url) return
        const url = this.$getConfig().bundleUrl
        navigator.push({
          url: util.setBundleUrl(url, 'page/web.js?weburl=' + _url),
          animated: 'true'
        })
      }

    }
  }
</script>
